local function _safe(s)
  if s == nil then return '' end
  s = tostring(s)
  s = string.gsub(s, '"', "'")
  s = string.gsub(s, "[\r\n\t]", " ")
  s = string.gsub(s, "|", "-")
  s = string.gsub(s, "  +", " ")
  return s
end

